// https://www.digitalocean.com/community/tutorials/how-to-build-forms-in-react refer to this resource to understand the code

import React, { useReducer, useState } from 'react';
import './App.css';

const formReducer = (state, event) => {
 return {
   ...state,
   [event.name]: event.value
 }
}

function App() {
  const [formData, setFormData] = useReducer(formReducer, {});
  const [submitting, setSubmitting] = useState(false);

  //below code is to simulate an API
  const handleSubmit = event => {
    event.preventDefault();
    setSubmitting(true);

    setTimeout(() => {
      setSubmitting(false);
    }, 3000)
  }

  const handleChange = event => {
    const isCheckbox = event.target.type === 'checkbox';
    setFormData({
      name: event.target.name,
      value: isCheckbox ? event.target.checked : event.target.value,
    });
  }

  return(
    <div className="wrapper">
      <h1>Proof of Story Protocol</h1>
      {submitting &&
        <div>
          You are submitting the following:
          <ul>
            {Object.entries(formData).map(([name, value]) => (
              <li key={name}><strong>{name}</strong>:{value.toString()}</li>
            ))}
          </ul>
        </div>
      }
      <form onSubmit={handleSubmit}>
        <fieldset>
          <label>
            <p>Name</p>
            <input name="name" onChange={handleChange}/>
          </label>
        </fieldset>


        <fieldset>
          <label>
            <p>Organisation Name</p>
            <input name="Org_name" onChange={handleChange}/>
          </label>
        </fieldset>

        <fieldset>
          <label>
            <p>Sender Wallet Address</p>
            <input name="Sender_address" onChange={handleChange}/>
          </label>
        </fieldset>

        <fieldset>
          <label>
            <p>Recepeint Wallet Address</p>
            <input name="Recepeint_address" onChange={handleChange}/>
          </label>
        </fieldset>

        <fieldset>
          <label>
            <p>Stream of work</p>
            <select name="Stream_of_work" onChange={handleChange}>
              <option value="">--Please choose an option--</option>
              <option value="fuji">Fuji</option>
              <option value="jonathan">Jonathan</option>
              <option value="honey-crisp">Honey Crisp</option>
            </select>
          </label>
          <label>
            <p>Count</p>
            <input type="number" name="count" onChange={handleChange} step="1" />
          </label>
          <label>
            <p>Gift Wrap</p>
            <input type="checkbox" name="gift-wrap" onChange={handleChange} />
          </label>
        </fieldset>

        <button type="submit">Submit</button>
      </form>
    </div>
  )
}

export default App;
